import React from 'react';
import '../styles/components.css';
import { LogoLoop } from './LogoLoop';

const About: React.FC = () => {
    const techLogos = [
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/javascript/javascript-original.svg', alt: 'JavaScript' },
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/typescript/typescript-original.svg', alt: 'TypeScript' },
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/react/react-original.svg', alt: 'React' },
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/nodejs/nodejs-original.svg', alt: 'Node.js' },
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/python/python-original.svg', alt: 'Python' },
        { src: '<svg preserveAspectRatio="xMidYMid" viewBox="0 0 256 346"><path d="M83 267s-14 8 9 11c27 3 41 2 71-3 0 0 8 5 19 9-67 29-153-2-99-17M74 230s-15 11 8 13c29 3 52 3 92-4 0 0 6 5 15 8-82 24-173 2-115-17" fill="#5382A1"/><path d="M144 166c17 19-4 36-4 36s42-22 22-49c-18-26-32-38 44-82 0 0-119 29-62 95" fill="#E76F00"/><path d="M233 295s10 8-10 15c-39 12-163 15-197 0-12-5 11-13 18-14l12-2c-14-9-89 19-38 28 138 22 251-10 215-27M89 190s-63 15-22 21c17 2 51 2 83-1 26-2 52-7 52-7l-16 9c-64 16-187 8-151-9 30-14 54-13 54-13M202 253c64-33 34-66 13-61l-7 2s2-3 6-5c41-14 73 43-14 66l2-2" fill="#5382A1"/><path d="M162 0s36 36-34 91c-56 45-12 70 0 99-32-30-56-56-40-80 23-35 89-53 74-110" fill="#E76F00"/><path d="M95 345c62 4 158-3 160-32 0 0-4 11-51 20-53 10-119 9-158 2 0 0 8 7 49 10" fill="#5382A1"/></svg>', alt: 'Java' },
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/angular/angular-original.svg', alt: 'Angular' },
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/git/git-original.svg', alt: 'Git' },
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/docker/docker-original.svg', alt: 'Docker' },
        { src: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/linux/linux-original.svg', alt: 'Linux' }
    ];

    return (
        <section className="about" id="about">
            <h2>./Sobre Mí</h2>
            <p>
                ¡Hola! Soy un protoingeniero Informático con sólida experiencia en el sector público, 
                especializado en análisis de sistemas y gestión de comunicaciones. Durante varios años 
                he trabajado en proyectos relacionados con telefonía fija y móvil, asegurando la 
                continuidad y eficiencia de las infraestructuras críticas.
            </p>
            <p>
                Mi enfoque combina conocimientos técnicos avanzados con capacidad para optimizar 
                procesos y garantizar soluciones seguras y escalables. Apasionado por la tecnología 
                y la mejora continua, busco aportar valor mediante innovación y compromiso en cada proyecto.
            </p>

            <h3>Tecnologías y Herramientas</h3>
            <div className="tech-logos-container">
                <LogoLoop
                    logos={techLogos}
                    speed={50}
                    direction="left"
                    logoHeight={48}
                    gap={40}
                    pauseOnHover={true}
                    scaleOnHover={true}
                    fadeOut={true}
                    fadeOutColor="var(--gb-light)"
                />
            </div>

            <h3>Habilidades</h3>
            <ul>
                <li>Análisis de Sistemas</li>
                <li>Gestión de Comunicaciones</li>
                <li>Telefonía Fija y Móvil</li>
                <li>Infraestructuras Críticas</li>
                <li>JavaScript / TypeScript</li>
                <li>React</li>
                <li>Node.js</li>
                <li>Optimización de Procesos</li>
            </ul>
        </section>
    );
};

export default About;
