import React from 'react';
import CharacterScene from './CharacterScene';
import '../styles/gameboy-theme.css';

const Home: React.FC = () => {
    const scrollToSection = (sectionId: string) => {
        const element = document.getElementById(sectionId);
        if (element) {
            element.scrollIntoView({ 
                behavior: 'smooth',
                block: 'start'
            });
        }
    };

    return (
        <div className="home-wrapper" id="home">
            <div className="home">
                <h1>¡Bienvenido a Mi Portfolio 8-Bit!</h1>
                <p>Explora mi trabajo y experiencias en el mundo de la tecnología.</p>

                <nav>
                    <ul>
                        <li onClick={() => scrollToSection('about')}>
                            <span>Sobre Mí</span>
                        </li>
                        <li onClick={() => scrollToSection('experience')}>
                            <span>Experiencia</span>
                        </li>
                        <li onClick={() => scrollToSection('projects')}>
                            <span>Proyectos</span>
                        </li>
                        <li onClick={() => scrollToSection('contact')}>
                            <span>Contacto</span>
                        </li>
                    </ul>
                </nav>

                {/* Componente de animación del personaje */}
                <CharacterScene />
            </div>
        </div>
    );
};

export default Home;
