import React from 'react';
import '../styles/gameboy-theme.css';
import '../styles/character-animation.css';

const Home: React.FC = () => {
    const scrollToSection = (sectionId: string) => {
        const element = document.getElementById(sectionId);
        if (element) {
            element.scrollIntoView({ 
                behavior: 'smooth',
                block: 'start'
            });
        }
    };

    return (
        <div className="home" id="home">
            <h1>Welcome to My 8-Bit Portfolio!</h1>
            <p>Explore my work and experiences in the world of technology.</p>

            {/* Escenario con personaje animado */}
            <div className="character-scene">
                {/* Nubes */}
                <div className="clouds">
                    <div className="cloud"></div>
                    <div className="cloud"></div>
                    <div className="cloud"></div>
                </div>

                {/* Elementos del escenario */}
                <div className="scenery">
                    <div className="tree">
                        <div className="tree-leaves"></div>
                        <div className="tree-trunk"></div>
                    </div>
                    <div className="tree">
                        <div className="tree-leaves"></div>
                        <div className="tree-trunk"></div>
                    </div>
                    <div className="tree">
                        <div className="tree-leaves"></div>
                        <div className="tree-trunk"></div>
                    </div>
                </div>

                {/* Personaje caminando */}
                <div className="character">
                    <div className="character-head">
                        <div className="character-eyes">
                            <span className="eye"></span>
                            <span className="eye"></span>
                        </div>
                    </div>
                    <div className="character-body"></div>
                    <div className="character-legs">
                        <div className="leg leg-left"></div>
                        <div className="leg leg-right"></div>
                    </div>
                </div>

                {/* Suelo animado */}
                <div className="ground"></div>
            </div>

            <nav>
                <ul>
                    <li onClick={() => scrollToSection('about')}>
                        <span>About Me</span>
                    </li>
                    <li onClick={() => scrollToSection('experience')}>
                        <span>Experience</span>
                    </li>
                    <li onClick={() => scrollToSection('projects')}>
                        <span>Projects</span>
                    </li>
                    <li onClick={() => scrollToSection('contact')}>
                        <span>Contact</span>
                    </li>
                </ul>
            </nav>
        </div>
    );
};

export default Home;
